figure.artwork:
(R.scheme, R.authority, R.path, R.query, fragment) = parse(R);
   -- The URI reference is parsed into the four components and
   -- fragment identifier, as described in Section 4.3.

if ((not validating) and (R.scheme == Base.scheme)) then
   -- For compatibility, a non-validating parser may ignore a scheme
   -- in the reference if it is identical to the base URI's scheme.
   undefine(R.scheme);
endif;

if defined(R.scheme) then
   T.scheme    = R.scheme;
   T.authority = R.authority;
   T.path      = R.path;
   T.query     = R.query;
else
   if defined(R.authority) then
      T.authority = R.authority;
      T.path      = R.path;
      T.query     = R.query;
   else
      if (R.path == "") then
         if defined(R.query) then
            T.path  = Base.path;
            T.query = R.query;
         else
            -- An empty reference refers to the current document
            return (current_document, fragment);
         endif;
      else
         if (R.path starts-with "/") then
            T.path = R.path;
         else
            T.path = merge(Base.path, R.path);
         endif;
         T.query = R.query;
      endif;
      T.authority = Base.authority;
   endif;
   T.scheme = Base.scheme;
endif;

return (T, fragment);

result = ""

if defined(T.scheme) then
   append T.scheme to result;
   append ":" to result;
endif;

if defined(T.authority) then
   append "//" to result;
   append T.authority to result;
endif;

append T.path to result;

if defined(T.query) then
   append "?" to result;
   append T.query to result;
endif;

if defined(fragment) then
   append "#" to result;
   append fragment to result;
endif;

return result;
